LastAA = 0
Attack = true
Move = true
ProjectileSpeed = GetRange(myHero) > 300 and GetProjectileSpeed(myHero) or math.huge
BaseWindupTime = 3
BaseAnimationTime = 0.65
DataUpdated = false

AfterAttackCallbacks = {}
OnAttackCallbacks = {}
BeforeAttackCallbacks = {}

function DisableAttacks()
	Attack = false
end

function EnableAttacks()
	Attack = true
end

function GetTarget()
  if SOWMenu.h.Combo:Value() then
    return GoS:GetTarget(MyRange(myHero), DAMAGE_PHYSICAL)
  elseif SOWMenu.h.Harass:Value() then
    for i=1, minionManager.maxObjects do
      local minion = minionManager.objects[i]
      if minion and IsObjectAlive(minion) and GetTeam(minion) ~= GetTeam(myHero) and GoS:IsInDistance(minion, MyRange(minion)) then
        local health = GoS:PredictHealth(minion, 1000*GoS:GetDistance(minion)/GetProjectileSpeed(myHero) + WindUpTime()*1000)
        if health < GetDmg(minion) and health > 0 then
          return minion
        end
      end
    end
    return ValidTarget(Target, MyRange(Target)) and Target or GoS:GetTarget(MyRange(Target), DAMAGE_PHYSICAL)
  elseif SOWMenu.h.LaneClear:Value() then
    local highestMinion, highestHealth = nil, 0
    for i=1, minionManager.maxObjects do
      local minion = minionManager.objects[i]
      if minion and IsObjectAlive(minion) and GetTeam(minion) ~= GetTeam(myHero) and GoS:IsInDistance(minion, MyRange(minion)) then
        local health = GoS:PredictHealth(minion, 1000*GoS:GetDistance(minion)/GetProjectileSpeed(myHero) + WindUpTime()*1000)
        local health2 = GoS:PredictHealth(minion, 2000*GoS:GetDistance(minion)/GetProjectileSpeed(myHero) + WindUpTime()*2000)
        if not highestMinion then highestMinion = minion highestHealth = health end
        if health > 0 then
          local dmg = GetDmg(minion) 
          if (health2 < 0 or health2 < dmg*2) and health > dmg and health ~= GetCurrentHP(minion) then
            return nil
          end
          if health < dmg then
            return minion
          elseif SOWMenu.lcm:Value() == 1 and health > highestHealth then
            highestHealth = health 
            highestMinion = minion
          elseif SOWMenu.lcm:Value() == 2 then
            if ValidTarget(Target) and GetObjectType(Target) ~= Obj_AI_Hero then
              return Target
            elseif health > highestHealth then
              highestHealth = health 
              highestMinion = minion
            end
          end
        end
      end
    end
    return highestMinion
  elseif SOWMenu.h.LastHit:Value() then
    for i=1, minionManager.maxObjects do
      local minion = minionManager.objects[i]
      if minion and IsObjectAlive(minion) and GetTeam(minion) ~= GetTeam(myHero) and GoS:IsInDistance(minion, MyRange(minion)) then
        local health = GoS:PredictHealth(minion, 1000*GoS:GetDistance(minion)/GetProjectileSpeed(myHero) + WindUpTime()*1000)
        if health < GetDmg(minion) and health > 0 then
          return minion
        end
      end
    end
  end
end

function GetTime()
	return GetTickCount()
end

function GetProjectileSpeed(unit)
  return projectilespeeds[GetObjectName(unit)]
end

function GetDmg(unit)
  return GoS:CalcDamage(myHero, unit, GetBonusDmg(myHero)+GetBaseDamage(myHero)) * 0.95
end

function MyRange(target)
	local myRange = GetRange(myHero) + GetHitBox(myHero)
	if target and ValidTarget(target) then
		myRange = myRange + GetHitBox(target)
	end
	return myRange - 20
end

function InRange(target)
	local MyRange = MyRange(target)
	if target and GoS:GetDistanceSqr(myHero,target) <= MyRange * MyRange then
		return true
	end
end

function ValidTarget(target)
	 if target == nil or GetOrigin(target) == nil or not IsTargetable(target) or IsImmune(target,myHero) or IsDead(target) or not IsVisible(target) then
		return false
	end
	return ValidTarget(target) and InRange(target)
end

function Attack(target)
	LastAA = GetTime() + Latency()
	AttackUnit(target)
end

function WindUpTime()
	return (1 / (GetAttackSpeed(myHero) * BaseWindupTime))
end

function AnimationTime()
	return (1 / (GetAttackSpeed(myHero) * BaseAnimationTime))
end

function Latency()
	return GetLatency() / 2000
end

function CanAttack()
	if LastAA <= GetTime() then
		return (GetTime() + Latency()  > LastAA + AnimationTime())
	end
	return false
end

function CanMove()
	if LastAA <= GetTime() then
		return (GetTime() + Latency() > LastAA + WindUpTime())
	end
end

function BeforeAttack(target)
	local result = false
	for i, cb in ipairs(BeforeAttackCallbacks) do
		local ri = cb(target, mode)
		if ri then
			result = true
		end
	end
	return result
end

function RegisterBeforeAttackCallback(f)
	table.insert(BeforeAttackCallbacks, f)
end

function OnAttack(target)
	for i, cb in ipairs(OnAttackCallbacks) do
		cb(target, mode)
	end
end

function RegisterOnAttackCallback(f)
	table.insert(OnAttackCallbacks, f)
end

function AfterAttack(target)
	for i, cb in ipairs(AfterAttackCallbacks) do
		cb(target, mode)
	end
end

function RegisterAfterAttackCallback(f)
	table.insert(AfterAttackCallbacks, f)
end

function OrbWalk(target)
	
	if Attacks and CanAttack() and ValidTarget(target) and not BeforeAttack(target) then
		Attack(target)
	elseif CanMove() and Move then
			local Target = GetTarget()
			if SOWMenu.h.Combo:Value() or not Target then
				local Mv = Vector(myHero) + 400 * (Vector(mousePos) - Vector(myHero)):normalized()
				MoveToXYZ(Mv.x, Mv.y, Mv.z)
		
		       end
	end
end

function IsAttack(SpellName)
	return (SpellName:lower():find("attack") or table.contains(AltAttacks, SpellName:lower())) and not table.contains(NotAttacks, SpellName:lower())
end

function resetAA()
	LastAA = 0
end

OnProcessSpell(function(unit, spell)
	if unit == myHero and IsAttack(spell.name) then
	
		if not DataUpdated and not spell.name:lower():find("card") then
			BaseAnimationTime = 1 / (spell.animationTime * GetAttackSpeed(myHero))
			BaseWindupTime = 1 / (spell.windUpTime * GetAttackSpeed(myHero))
		
			DataUpdated = true
		end
		LastAA = GetTime() - Latency()
		checking = true
		LastAttackCancelled = false
		OnAttack(spell.target)
		checkcancel = GetTime()
		GoS:DelayAction(function(x) AfterAttack(x) end, WindUpTime() - Latency(), {spell.target})

	elseif unit == myHero and spell.name == AAResets[spell.name:lower()] then
		GoS:DelayAction(function() resetAA() end, 250)
	end
end)

OnLoop(function(myHero)
  local mousePos = GetMousePos()
  local target = GetTarget()
  if SOWMenu.h.Combo:Value() and target then
  OrbWalk(target)
  end
end)
